msg.parsed = {
    property: topicParts[2],
    type: topicParts[2].substring(0, 1).toUpperCase() +
        topicParts[2].substring(1) + "Sensor",
    id: topicParts[3],
//  ...payload
    timestamp: msg.payload.t,
    value: msg.payload.v,
    unit: msg.payload.u
};

return msg;

msg.payload = keccak256(msg.payload);

node.status({
    fill: "blue",
    shape: "ring",
    text: "0x" + msg.payload.toString('hex')
});

return msg;

