// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.7.0 <0.9.0;

// On REMIX, use CRTL+SHIFT+S to Compile and Deploy (and Test)

/**
 * @title MonitoringDataNotarization
 * @author 0x7efC9d38581c7Cb8372325c349Ec5095D027B3Dd
 * @dev Notarization of monitoring data via Blockchain
 * @custom:dev-run-script ./scripts/deploy_with_web3(MDN).ts
 */
contract MonitoringDataNotarization {

    struct Measurement {
        string label; // unencrypted metadata (e.g., query, Influx tags, json or array of attributes, etc.) 
        bytes32 data; // encoded monitoring data (e.g., dump, Influx fields, json or array of values, ect.)
    }

    mapping (address => mapping (uint => Measurement)) private measurements; // i.e., (source, timestamp) --> {label, data}
    uint private latestRegisteredTimestamp;

    uint16 private timeslot;
    address private owner;

    event Registered(uint, Measurement);

    constructor(uint16 timeslotInSeconds) {
        owner = msg.sender;
        timeslot = timeslotInSeconds;
    }

    modifier onlyOwner() {
        require(msg.sender == owner, "Only owner authorized");
        _;
    }

    // ----------------
    // HELPER FUNCTIONS
    // ----------------

    /// @dev Get timeslot duration
    function getTimeslot() 
            external view returns (uint16 inSeconds) {
        return timeslot;      
    }

    /// @dev Encode binary data with SHA256 hash
    function encodeSHA2(bytes memory data)
            external pure returns (bytes32 hash) {
        return sha256(data);
    }

    /// @dev Encode binary data with SHA256 hash
    function encodeSHA3(bytes memory data)
            public pure returns (bytes32 hash) {
        return keccak256(data);
    }

    /// @dev Encode string data with SHA256 hash
    function encodeString(string calldata text)
            public pure returns (bytes32 hash) {
        return encodeSHA3(abi.encode(text));
    }

    /// @dev Normalize a timestamp (e.g., 12:45:00, if timestamp = 12:34:56 and timeslot = 15 min) 
    function normalize(uint timestamp)
            public view returns (uint inSeconds) {
        return timestamp / timeslot * timeslot;
    }

    /// @dev Get the current timestamp
    function getTimestamp()
            public view returns (uint inSeconds) {
        return block.timestamp;
    }

    /// @dev Get the latest registered timestamp
    function getLatestRegisteredTimestamp()
            public view returns (uint inSeconds) {
        return latestRegisteredTimestamp;
    }

    // ----------------------
    // NOTARIZATION FUNCTIONS
    // ----------------------

    /// @dev Notarize measurement data (in a normalized timeslot for a given data source)
    function register(address source, uint timestamp, string calldata label, bytes32 data) 
            public returns (uint, Measurement memory) {
        require(data != "", "Data cannot be null");
        require(measurements[source][timestamp].data == "", "Data already notarized");
        require(timestamp > 0 && timestamp % timeslot == 0, "Timestamp must be normalized");

        measurements[source][timestamp] = Measurement(label, data);
        latestRegisteredTimestamp = timestamp;

        emit Registered(timestamp, measurements[source][timestamp]);
        return (timestamp, measurements[source][timestamp]); // works only if called by a contract, externally use events
    }

    /// @dev Notarize measurement data with the sender as data source 
    function register(uint timestamp, string calldata label, bytes32 data) 
            external returns (uint, Measurement memory) {
        return register(msg.sender, timestamp, label, data);
    }

    /// @dev Normalize the current timestamp, encode the given data and notarize them for the sender as data source 
    function notarize(string calldata label, string calldata data) 
            external returns (uint timestamp, Measurement memory) {
        return register(msg.sender, normalize(getTimestamp()), label, encodeString(data));
    }

    // ----------------------
    // VERIFICATION FUNCTIONS
    // ----------------------

    /// @dev Verify that measurement data have been notarized for the given data souce and timestamp
    function check(address source, uint timestamp, bytes32 data)
            public view returns (bool) {
        return measurements[source][timestamp].data == data;
    }

    /// @dev Get the notarized measurement at the given timestamp
    function getRegistation(address source, uint timestamp)
            public view returns (Measurement memory) { 
        return measurements[source][timestamp];
    }

    /// @dev Verify measurement data for the sender as data source
    function check(uint timestamp, bytes32 data)
            external view returns (bool) {
        return check(msg.sender, timestamp, data);
    }

    /// @dev Get the notarized measurement at the given timestamp
    function getRegistration(uint timestamp)
            external view returns (Measurement memory) { 
        return getRegistation(msg.sender, timestamp);
    }

    /// @dev Encode data and verify them for the sender as data source
    function verify(uint timestamp, string calldata data)
            external view returns (bool) {
        return check(msg.sender, timestamp, encodeString(data));
    }
}

