 * @dev Notarization of documents, with nullify and get support, custom metadata and state change history.
 * @custom:dev-run-script ./scripts/deploy_with_ethers_(PoE).ts
 */
contract ProofOfExistence {
    // bytes32 private constant EMPTY_STRING = 0xc5d2460186f7233c927e7db2dcc703c0e500b653ca82273b7bfad8045d85a470;

    /// @dev Proof of Existence (PoE) of a document.
    struct Proof {
        string description; // unencrypted public metadata attached (optional)
        address provider; // the address of the original tx sender (tx.origin)
        uint256 timestamp; // tx instant (obtained from each containing block)
        uint256 blockNumber; // to find tx info via explorer (e.g., Etherscan)
        bool isNotarized; // proof state (true = notarized, false = nullified)
    }

    /// @dev List of all registered proofs of existence.
    mapping(bytes32 => Proof[]) proofs;

    /// @dev Notifies the notarization of each document.
    event Notarized(bytes32, Proof);

    /// @dev Notifies the nullifications of every proof.
    event Nullified(bytes32, Proof);

    modifier onlyNotarized(bytes32 hash) {
        require(getProof(hash).isNotarized, "Not notarized.");
        _;
    }

    modifier onlyNotNotarized(bytes32 hash) {
        require(!getProof(hash).isNotarized, "Already notarized.");
        _;
    }

    modifier onlyNotEmpty(string calldata document) {
        require(bytes(document).length != 0, "Empty string not allowed.");
        _;
    }

    modifier onlyNotNull(bytes32 hash) {
        require(hash.length != 0, "Null hash not allowed.");
        _;
    }

    modifier onlyOriginalSender(bytes32 hash) {
        require(getProof(hash).provider == tx.origin, "Only original sender allowed.");
        _;
    }

    // ==================== //
    //  EXTERNAL FUNCTIONS  //
    // ==================== //

    /// @dev Calculate the hash from the content and register the proof of a document with metadata.
    /// @param document The content of the document
    /// @param metadata Unencrypted custom description attached to the proof
    /// @return hash The hash of the document
    /// @return proof The proof of the document
    function certify(string calldata document, string calldata metadata) external
        returns (bytes32 hash, Proof memory proof)
    {
        hash = digest(document);
        proof = notarize(hash, metadata);
        return (hash, proof);
    }

    /// @dev Calculate the hash from the content and register the proof of a document.
    /// @param document The content of the document
    /// @return hash The hash of the document
    /// @return proof The proof of the document
    function certifyDocumentOnly(string calldata document) external onlyNotEmpty(document)
        returns (bytes32 hash, Proof memory proof)
    {
        hash = digest(document);
        proof = notarizeHashOnly(hash);
        return (hash, proof);
    }

    /// @dev Calculate the hash from the content and deregister the proof of a notarized document with metadata.
    /// @param document The content of the document
    /// @param metadata Unencrypted custom description attached to the proof
    /// @return hash The hash of the document
    /// @return proof The proof of the document
    function cancel(string calldata document, string calldata metadata) external onlyNotEmpty(document)
        returns (bytes32 hash, Proof memory proof)
    {
        hash = digest(document);
        proof = nullify(hash, metadata);
        return (hash, proof);
    }

    /// @dev Calculate the hash from the content and deregister the proof of a notarized document.
    /// @param document The content of the document
    /// @return hash The hash of the document
    /// @return proof The proof of the document
    function cancelDocumentOnly(string calldata document) external onlyNotEmpty(document)
        returns (bytes32 hash, Proof memory proof)
    {
        hash = digest(document);
        proof = nullifyHashOnly(hash);
        return (hash, proof);
    }

    // ======================== //
    //  EXTERNAL VIEW FUNCTIONS //
    // ======================== //

    /// @dev Calculate the hash from the content and check the state of the proof of a document.
    /// @param document The content of the document
    /// @return state Latest state of the proof (`true` = notarized, `false` = nullified)
    function check(string calldata document) external view onlyNotEmpty(document)
        returns (bool state)
    {
        state = verify(digest(document));
        return state;
    }

    // ================== //
    //  PUBLIC FUNCTIONS  //
    // ================== //

    /// @dev Register the proof of a document throgh its hash with metadata.
    /// @param hash The hash of the document
    /// @param metadata Unencrypted custom description attached to the proof
    /// @return proof The proof of the document
    function notarize(bytes32 hash, string calldata metadata) public onlyNotNotarized(hash)
        returns (Proof memory proof)
    {
        proof = register(hash, metadata, tx.origin, true);
        emit Notarized(hash, proof);
        return proof;
    }

    /// @dev Register the proof of a document throgh its hash.
    /// @param hash The hash of the document
    /// @return proof The proof of the document
    function notarizeHashOnly(bytes32 hash) public onlyNotNotarized(hash)
        returns (Proof memory proof)
    {
        proof = register(hash, tx.origin, true);
        emit Notarized(hash, proof);
        return proof;
    }

    /// @dev Deregister the proof of a notarized document throgh its hash with metadata.
    /// @param hash The hash of the document
    /// @param metadata Unencrypted custom description attached to the proof
    /// @return proof The proof of the document
    function nullify(bytes32 hash, string calldata metadata) public onlyNotarized(hash) onlyOriginalSender(hash)
        returns (Proof memory proof)
    {
        proof = register(hash, metadata, tx.origin, false);
        emit Nullified(hash, proof);
        return proof;
    }

    /// @dev Deregister the proof of a notarized document throgh its hash.
    /// @param hash The hash of the document
    /// @return proof The proof of the document
    function nullifyHashOnly(bytes32 hash) public onlyNotarized(hash) onlyOriginalSender(hash)
        returns (Proof memory proof)
    {
        proof = register(hash, tx.origin, false);
        emit Nullified(hash, proof);
        return proof;
    }

    // ====================== //
    //  PUBLIC VIEW FUNCTIONS //
    // ====================== //

    /// @dev Return the change history of the proof of a document throgh its hash.
    /// @param hash The hash of the document
    /// @return history The list of proof changes
    function getHistory(bytes32 hash) public view
        returns (Proof[] memory history)
    {
        history = proofs[hash];
        return history;
    }

    /// @dev Return the proof of a document throgh its hash.
    /// @param hash The hash of the document
    /// @return proof The proof of the document
    function getProof(bytes32 hash) public view
        returns (Proof memory proof)
    {
        if (proofs[hash].length > 0)
            proof = proofs[hash][proofs[hash].length - 1];
        return proof;
    }

    /// @dev Return the state of the proof of a document throgh its hash.
    /// @param hash The hash of the document
    /// @return state Latest state of the proof (`true` = notarized, `false` = nullified)
    function verify(bytes32 hash) public view
        returns (bool state)
    {
        state = getProof(hash).isNotarized;
        return state;
    }

