var { heapUsed, heapTotal } = process.memoryUsage();
var p_uptime = process.uptime();
var loadavg = os.loadavg();

cpuUsage(perc => node.send({
    payload: {
        cpu_usage:    perc,
        cpu_user: user / 1000 / 1000,
        cpu_system: system / 1000 / 1000,

        used_heap_MB: round(heapUsed / 1024 / 1024),
        heap_usage: round(heapUsed / heapTotal * 100),
        tot_heap_MB: round(heapTotal / 1024 / 1024),
        free_heap_MB: round((heapTotal - heapUsed) / 1024 / 1024),

        free_ram_GB: round(freemem / 1024 / 1024 / 1024),
        tot_ram_GB: round(totalmem / 1024 / 1024 / 1024),
        ram_usage: round((1 - freemem / totalmem) * 100),
        used_ram_GB: round((totalmem - freemem) / 1024 / 1024 / 1024),

        loadavg_1: loadavg[0],
        loadavg_5: loadavg[1],
        loadavg_15: loadavg[2],

        os_uptime_s: os_uptime,
        os_uptime: timeConversion(os_uptime),
        p_uptime_s: p_uptime,
        p_uptime: timeConversion(p_uptime),

        arch: os.arch(),
        cpus: os.cpus(),
        cpu_model: os.cpus()[os.cpus().length - 1].model,
        cpu_speed: os.cpus()[os.cpus().length - 1].speed,
        n_cores: os.cpus().length,
        endianness: os.endianness(),
        homedir: os.homedir(),
        hostname: os.hostname(),
        network: os.networkInterfaces(),
        address: os.networkInterfaces().eth0[0].address,
        platform: os.platform(),
        release: os.release(),
        type: os.type(),
        userInfo: os.userInfo(),
        gid: os.userInfo().gid,
        uid: os.userInfo().uid,
        username: os.userInfo().username,
        shell: os.userInfo().shell,
        os_version: os.version(),

        cwd: process.cwd(),
        node_version: process.version,

        last_update: new Date()
    }
}))

/**
 * Method used in node-os-utils
 * 
 * @description Measure the CPU usage calculated between two different CPU usage samples
 * @param {function} cb A callback function the result is passed to
 * @param {number} [core=-1] The CPU core index to measure. When is not greater than -1 then it is "averaged" for all CPU cores.
 * @param {number} [sampleMs=1000]  The number of milliseconds between the CPU usage samples
 */
function cpuUsage(cb, core, sampleMs) {
    var deltaUsed;
    var deltaIdle;
    var timesKeys = ["user", "nice", "sys", "irq"];
    var allCores = null === core || !(core > -1);

    var byCore = (cpu, i) =>
        allCores || core === i;

    var bySampleKey = (sample, key) =>
        sample.filter(byCore).reduce(
            (sum, cpu) => sum + cpu.times[key], 0);

    var sample0 = os.cpus();

    setTimeout(function () {
        var sample1 = os.cpus();

        deltaUsed = timesKeys.reduce(
            (diff, key) => diff + bySampleKey(sample1, key) - bySampleKey(sample0, key), 0);

        deltaIdle = bySampleKey(sample1, "idle") - bySampleKey(sample0, "idle");

        if ("function" === typeof cb) {
            cb(100 * (deltaUsed / (deltaUsed + deltaIdle)));
        }
    }, sampleMs || 1000);
}

function round(n, d = 2) {
    return Math.round(n * 10 ** d) / 10 ** d;
}

function timeConversion(seconds) {
    var minutes = seconds / 60;
    var hours = seconds / 3600;
    var days = seconds / 86400;

    if (seconds < 60) {
        return seconds.toFixed(1) + " Sec";
    } else if (minutes < 60) {
        return minutes.toFixed(1) + " Min";
    } else if (hours < 24) {
        return hours.toFixed(1) + " Hrs";
    } else {
        return days.toFixed(1) + " Days"
    }
}

